mapper.select:
SELECT admin_id, admin_pass
FROM admin
WHERE admin_id = #{admin_id}

mapper.update:
UPDATE admin
SET admin_pass = #{newPass}, admin_nickname = #{admin_nickname}, admin_birth = #{admin_birth}
WHERE admin_id = #{admin_id}

