mapper.select:
select * 
from admin_csnotice
ORDER BY admin_cs_num DESC;

mapper.update:
UPDATE admin_csnotice
SET admin_csnotice_sub=#{admin_csnotice_sub}, 
	admin_cs_view=#{admin_cs_view}
WHERE admin_cs_num=#{admin_cs_num}

mapper.select:
SELECT * 
FROM admin_blog
ORDER BY admin_blog_num DESC;

mapper.update:
UPDATE admin_blog SET admin_blog_mainTitle = #{admin_blog_mainTitle}, admin_blog_subTitle = #{admin_blog_subTitle}, admin_blog_content = #{admin_blog_content}, admin_blog_file =
#{file}
WHERE admin_blog_num = #{admin_blog_num}

mapper.select:
SELECT * 
FROM admin_blog
ORDER BY admin_blog_num ASC;

