mapper.select:
select * from member
where member_id = #{member_id}

  and member_pass = #{member_pass}


  	SELECT *
	FROM  cart
  	WHERE member_num = #{member_num}
  	AND item_num = #{item_num}
  	LIMIT 1;

mapper.update:
UPDATE cart as c
SET	cart_cnt = c.cart_cnt + #{cart_cnt}
WHERE item_num = #{item_num}
AND member_num = #{member_num}

mapper.delete:
DELETE 
FROM cart
WHERE item_num = #{item_num} 
AND member_num = #{member_num}
