mapper.select:
  	SELECT one_board.one_board_num AS id, one_board.one_board_title AS title, members.name AS author, one_board.one_board_day AS date
	FROM one_board
	JOIN members ON one_board.member_num = members.member_num;


  	SELECT *
	FROM  cart
  	WHERE member_num = #{member_num}
  	AND item_num = #{item_num}
  	LIMIT 1;

mapper.update:
UPDATE cart as c
SET	cart_cnt = c.cart_cnt + #{cart_cnt}
WHERE item_num = #{item_num}
AND member_num = #{member_num}
