mapper.select:
SELECT s.seller_num, s.seller_storeName, s.seller_name, FORMAT(SUM(o.order_pay), 0) AS total_revenue, FORMAT(floor(sum(o.order_pay) * 0.05), 0) as fee, FORMAT(SUM(o.order_pay) - floor(sum(o.order_pay) * 0.05), 0) as settlement_amount,
FORMAT((SELECT SUM(o.order_pay) FROM orders o INNER JOIN item i ON i.item_num = o.item_num INNER JOIN seller s ON s.seller_num = i.seller_num WHERE s.seller_num != 'admin'), 0) as grand_total_revenue, FORMAT((SELECT SUM(floor(o.order_pay * 0.05)) FROM orders o INNER JOIN item i ON i.item_num = o.item_num INNER JOIN seller s ON s.seller_num = i.seller_num WHERE s.seller_num != 'admin'), 0) as grand_fee, FORMAT((SELECT SUM(o.order_pay) - SUM(floor(o.order_pay * 0.05)) FROM orders o INNER JOIN item i ON i.item_num = o.item_num INNER JOIN seller s ON s.seller_num = i.seller_num WHERE s.seller_num != 'admin'), 0) as grand_settlement_amount
FROM seller s LEFT JOIN item i ON s.seller_num = i.seller_num LEFT JOIN orders o ON i.item_num = o.item_num WHERE s.seller_num != 'admin' GROUP BY s.seller_num, s.seller_id, s.seller_name;

mapper.update:
UPDATE settlement
SET settlement_yn = 'Y'
WHERE seller_num IN

    #{item}

AND DATE_FORMAT(order_month, '%Y-%m') = #{orderMonth}
